messages = ''
for item in jsonData :
    #만약에 item(jsonData)(요소)내 'message'키가 존재하면
    if 'message' in item.keys() :
        # print(item)
        #전처리(정규표현식)
        # print(item['message']) #분석할 문자
        #분석할 문자 내 정규표현식을 이용한 특수 문자를 제거 하고 공백 으로 치환 한다.
        messages = messages + re.sub(r'[^\w]',' ', item['message']) #특수 문자 정규표현식을 이용해서 제거 .replace("\n" , " ") 과 같음
