#word_count = dict{} vs word_count = {} # 같음
#most_common(n) : 상위 n개 추출
for tag,count in wordCount.most_common(80) :
    if len(tag) > 1 : #만약 단어의 길이가 1글자인 경우 제외
        word_count[tag] = count
        #{'단어' : 빈도수}
